import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import styled from 'styled-components';
import './App.css';
import Login from './components/Login';

const AppContainer = styled.div`
  background-color: #1a1a1a;
  min-height: 100vh;
  color: white;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  padding: 20px;
`;

const UserProfile = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 30px;
`;

const Avatar = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 15px;
  font-size: 24px;
`;

const UserInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

const UserName = styled.h2`
  margin: 0;
  font-size: 20px;
  font-weight: 600;
  color: white;
`;

const UserDescription = styled.p`
  margin: 5px 0 0 0;
  font-size: 14px;
  color: #888;
`;

const RoomInfo = styled.div`
  background-color: #2a2a2a;
  border-radius: 12px;
  padding: 20px;
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const RoomIcon = styled.div`
  width: 40px;
  height: 40px;
  background-color: #4a90e2;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 15px;
  font-size: 20px;
`;

const RoomName = styled.h1`
  margin: 0;
  font-size: 24px;
  font-weight: 600;
  color: white;
`;

const DateInfo = styled.div`
  font-size: 16px;
  color: white;
  margin-bottom: 30px;
`;

const TimeSlotList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const TimeSlot = styled.div`
  background-color: #2a2a2a;
  border-radius: 12px;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const TimeInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

const TimeRange = styled.span`
  font-size: 16px;
  font-weight: 500;
  color: white;
  margin-bottom: 5px;
`;

const TimeStatus = styled.span`
  font-size: 14px;
  color: #888;
`;

const ReserveButton = styled.button`
  background-color: #4a4a4a;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #5a5a5a;
  }

  &:disabled {
    background-color: #3a3a3a;
    color: #666;
    cursor: not-allowed;
  }
`;

const LogoutButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background-color: #e74c3c;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 8px 16px;
  font-size: 14px;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #c0392b;
  }
`;

function App() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [timeSlots, setTimeSlots] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await fetch('/auth/status', {
        credentials: 'include'
      });
      if (response.ok) {
        const userData = await response.json();
        setIsLoggedIn(true);
        setUser(userData);
      }
    } catch (error) {
      console.log('Ïù∏Ï¶ù ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch('/auth/logout', {
        method: 'POST',
        credentials: 'include'
      });
      setIsLoggedIn(false);
      setUser(null);
    } catch (error) {
      console.log('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
    }
  };

  // ÏãúÍ∞ÑÎåÄ ÏÉùÏÑ± (Ïò§Ï†Ñ 10ÏãúÎ∂ÄÌÑ∞ Ïò§ÌõÑ 3ÏãúÍπåÏßÄ)
  useEffect(() => {
    const slots = [];
    for (let hour = 10; hour <= 15; hour++) {
      const startHour = hour;
      const endHour = hour + 1;

      const startTime = `${startHour < 12 ? 'Ïò§Ï†Ñ' : 'Ïò§ÌõÑ'} ${startHour < 12 ? startHour : startHour - 12}:00`;
      const endTime = `${endHour < 12 ? 'Ïò§Ï†Ñ' : 'Ïò§ÌõÑ'} ${endHour < 12 ? endHour : endHour - 12}:00`;

      slots.push({
        id: hour,
        timeRange: `${startTime} - ${endTime}`,
        isPast: false, // Ïã§Ï†úÎ°úÎäî ÌòÑÏû¨ ÏãúÍ∞ÑÍ≥º ÎπÑÍµêÌï¥Ïïº Ìï®
        isReserved: false
      });
    }
    setTimeSlots(slots);
  }, []);

  const handleReserve = (slotId) => {
    console.log(`ÏòàÏïΩ ÏöîÏ≤≠: ${slotId}Î≤à ÏãúÍ∞ÑÎåÄ`);
    // Ïó¨Í∏∞Ïóê ÏòàÏïΩ Î°úÏßÅ Ï∂îÍ∞Ä
  };

  const formatDate = (date) => {
    const months = ['1Ïõî', '2Ïõî', '3Ïõî', '4Ïõî', '5Ïõî', '6Ïõî', '7Ïõî', '8Ïõî', '9Ïõî', '10Ïõî', '11Ïõî', '12Ïõî'];
    const days = ['Ïùº', 'Ïõî', 'Ìôî', 'Ïàò', 'Î™©', 'Í∏à', 'ÌÜ†'];

    return `Ïò§ÎäòÏùÄ ${months[date.getMonth()]} ${date.getDate()}Ïùº (${days[date.getDay()]})`;
  };

  const ReservationPage = () => (
    <AppContainer>
      {isLoggedIn && (
        <LogoutButton onClick={handleLogout}>
          Î°úÍ∑∏ÏïÑÏõÉ
        </LogoutButton>
      )}

      <UserProfile>
        <Avatar>üê∞</Avatar>
        <UserInfo>
          <UserName>{user ? user.displayName : 'Í≥†ÎØºÏßÄ'}</UserName>
          <UserDescription>ÌÅ¨ÎûòÌîÑÌÜ§ Ï†ïÍ∏Ä 9Í∏∞</UserDescription>
        </UserInfo>
      </UserProfile>

      <RoomInfo>
        <RoomIcon>üì¶</RoomIcon>
        <RoomName>303 ÏΩîÏπ≠Ïã§</RoomName>
      </RoomInfo>

      <DateInfo>
        {formatDate(currentDate)}
      </DateInfo>

      <TimeSlotList>
        {timeSlots.map((slot) => (
          <TimeSlot key={slot.id}>
            <TimeInfo>
              <TimeRange>{slot.timeRange}</TimeRange>
              <TimeStatus>Ïù¥ÎØ∏ ÏßÄÎÇú ÏãúÍ∞ÑÏù¥ÏóêÏöî</TimeStatus>
            </TimeInfo>
            <ReserveButton 
              onClick={() => handleReserve(slot.id)}
              disabled={slot.isPast}
            >
              ÏòàÏïΩ
            </ReserveButton>
          </TimeSlot>
        ))}
      </TimeSlotList>
    </AppContainer>
  );

  return (
    <Router>
      <Routes>
        <Route 
          path="/" 
          element={isLoggedIn ? <ReservationPage /> : <Navigate to="/login" />} 
        />
        <Route 
          path="/login" 
          element={isLoggedIn ? <Navigate to="/" /> : <Login />} 
        />
        <Route 
          path="/auth/github/callback" 
          element={<Navigate to="/" />} 
        />
      </Routes>
    </Router>
  );
}

export default App;
